package com.CoreJava;

public class UniaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=10;
		x++;
		System.out.println(x);//11
		System.out.println(++x);//12
		System.out.println(x--);//12
		System.out.println(--x);//10
		
	}


	
		
		
		
		
	}
	
	
	
	
	

